  bool access::object_traits_impl< ::employee, id_mysql >::
  grow (image_type& i,
        my_bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id_
    //
    t[0UL] = 0;

    // first_
    //
    if (t[1UL])
    {
      i.first_value.capacity (i.first_size);
      grew = true;
    }

    // last_
    //
    if (t[2UL])
    {
      i.last_value.capacity (i.last_size);
      grew = true;
    }

    // age_
    //
    t[3UL] = 0;

    // residence_
    //
    if (t[4UL])
    {
      i.residence_value.capacity (i.residence_size);
      grew = true;
    }

    // nationality_
    //
    if (t[5UL])
    {
      i.nationality_value.capacity (i.nationality_size);
      grew = true;
    }

    // employed_by_
    //
    t[6UL] = 0;

    return grew;
  }

  void access::object_traits_impl< ::employee, id_mysql >::
  bind (MYSQL_BIND* b,
        image_type& i,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    std::size_t n (0);

    // id_
    //
    if (sk != statement_update)
    {
      b[n].buffer_type = MYSQL_TYPE_LONGLONG;
      b[n].is_unsigned = 1;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // first_
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.first_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.first_value.capacity ());
    b[n].length = &i.first_size;
    b[n].is_null = &i.first_null;
    n++;

    // last_
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.last_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.last_value.capacity ());
    b[n].length = &i.last_size;
    b[n].is_null = &i.last_null;
    n++;

    // age_
    //
    b[n].buffer_type = MYSQL_TYPE_SHORT;
    b[n].is_unsigned = 1;
    b[n].buffer = &i.age_value;
    b[n].is_null = &i.age_null;
    n++;

    // residence_
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.residence_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.residence_value.capacity ());
    b[n].length = &i.residence_size;
    b[n].is_null = &i.residence_null;
    n++;

    // nationality_
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.nationality_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.nationality_value.capacity ());
    b[n].length = &i.nationality_size;
    b[n].is_null = &i.nationality_null;
    n++;

    // employed_by_
    //
    b[n].buffer_type = MYSQL_TYPE_LONGLONG;
    b[n].is_unsigned = 1;
    b[n].buffer = &i.employed_by_value;
    b[n].is_null = &i.employed_by_null;
    n++;
  }

